INSERT INTO "Publicacao" (publicacaoID,editoraID,subcategoriaID,titulo,dataPublicacao,codigoBarras,descricao,peso,preco,precoPromocional,novidade,stock,edicao,periodicidade,ISBN) VALUES (91,10,56,'Dicionário Moderno de Espanhol-Português / Português-Espanhol','01/01/2016','codigoBarras','Um dicionário de língua espanhola, cujo texto em português se encontra adaptado ao Acordo Ortográfico de 1990. Para facilitar a pesquisa e a transição segura para a nova ortografia, na lista de entradas da parte Português-Espanhol foram registadas as duas grafias: as grafias anteriores à reforma ortográfica e as novas grafias introduzidas por ela.','1,040','17,90','16,11','true',7,'primeira',NULL,'978-972-0-05755-6');
